mapper.insert:

	SELECT TO_CHAR(SEQ_SCHEDULE_NO.NEXTVAL) FROM DUAL


INSERT INTO SCHEDULE (
SCHEDULE_NO
, SCHEDULE_TITLE
, SCHEDULE_TYPE_NO
, START_DATE
, END_DATE
, START_TIME
, END_TIME
, SCHEDULE_DESP
, LEC_NO
, SCHEDULE_TARGET_ID
, IS_NOTICE
) VALUES (
#{scheduleNo}
, #{scheduleTitle}
, #{type.scheduleTypeNo}
, #{startDate}
, #{endDate}
, #{startTime}
, #{endTime}
, #{scheduleDesp}
, #{lecNo}
, #{scheduleTargetId}
, #{isNotice}
)

mapper.update:
UPDATE SCHEDULE
SET
	SCHEDULE_TITLE = #{scheduleTitle}
	, SCHEDULE_TYPE_NO = #{type.scheduleTypeNo}
	, START_DATE = #{startDate}
	, END_DATE = #{endDate}
	, START_TIME = #{startTime}
	, END_TIME = #{endTime}
	, SCHEDULE_DESP = #{scheduleDesp}
	, LEC_NO = #{lecNo}
	, SCHEDULE_TARGET_ID = #{scheduleTargetId}
	, IS_NOTICE = #{isNotice}
WHERE
	SCHEDULE_NO = #{scheduleNo}

mapper.delete:
		UPDATE SCHEDULE
    	SET IS_ACTIVE = 'N'
   		WHERE SCHEDULE_NO = #{scheduleNo}

mapper.select:
SELECT COUNT(*)
FROM SCHEDULE
WHERE SCHEDULE_NO = #{scheduleNo}
