mapper.insert:
INSERT ALL
    INTO TB_USER (
        USER_NO, USER_NAME, USER_PASS, GENDER, USER_REGNO1, USER_REGNO2,
        USER_TEL, USER_ZIP, USER_ADD1, USER_ADD2, USER_EMAIL, 
        BANK_CD, USER_ROLE, USER_BANKNO
    )
    VALUES (
        'ST' || LPAD(SEQ_STUDENT.NEXTVAL, 8, '0'), #{userName}, #{userPass}, #{gender}, #{userRegno1}, #{userRegno2},
        #{userTel}, #{userZip}, #{userAdd1}, #{userAdd2}, #{userEmail},
        #{bankCd}, #{userRole}, #{userBankno}
    )
    INTO STUDENT (
        USER_NO, STUDENT_GRADE, STUDENT_EDATE, 
        STUDENT_EMP, DEPARTMENT_NO
    )
    VALUES (
        'ST' || LPAD(SEQ_STUDENT.NEXTVAL, 8, '0'), #{studentGrade}, #{studentEdate},
        #{studentEmp}, #{departmentNo}
    )
SELECT * FROM DUAL

mapper.update:
	UPDATE STUDENT SET
        STUDENT_GRADE = #{studentGrade},
        DEPARTMENT_NO = #{departmentNo},
        STUDENT_EMP = #{studentEmp},
        STU_STATUS = #{stuStatus}
    WHERE USER_NO = #{userNo}
